mapper.select:

select * from busdata  
where toDestination =#{to}
and fromDestination =#{from} 
and filterDate =#{date}
order By filterDate desc

mapper.update:

update busdata set filterDate = #{filterDate},toDestination = #{toDestination}
fromDestination = #{fromDestination},price = #{price},BusName = #{BusName},time = #{time}
where id = #{id}
